plugins {
    // Apply the application plugin to add support for building a CLI application in Java.
    application
    id("org.flywaydb.flyway") version "10.0.0"
}

repositories {
    mavenCentral()
}

dependencies {
    // Use JUnit Jupiter for testing.
    testImplementation(libs.junit.jupiter)

    testRuntimeOnly("org.junit.platform:junit-platform-launcher")

    // runtimeOnly("org.flywaydb:flyway-database-postgresql:10.9.1")


    // This dependency is used by the application.
    implementation(libs.guava)
    implementation(libs.javalin)
    implementation(libs.slf4j)
    implementation(libs.postgresql)
    implementation(libs.jackson)
}

// Apply a specific Java toolchain to ease working on different environments.
java {
    toolchain {
        languageVersion = JavaLanguageVersion.of(21)
    }
}

application {
    // Define the main class for the application.
    mainClass = "org.example.App"
}

tasks.named<Test>("test") {
    // Use JUnit Platform for unit tests.
    useJUnitPlatform()
}

buildscript {
    dependencies {
        classpath("org.flywaydb:flyway-database-postgresql:10.9.1")

    }
}

flyway {
    url = "jdbc:postgresql://localhost:5432/todoapp"
    user = "tolumideshopein"
    password = "postgres"
    driver = "org.postgresql.Driver"
    baselineOnMigrate = true
    locations = arrayOf("filesystem:src/main/resources/db/migration/")
    // locations = arrayOf("classpath:db/migration")
}


tasks.register("printClasspath") {
    doLast {
        configurations["runtimeClasspath"].forEach { println(it) }
    }
}